import React, { useEffect, useState } from "react";
import { fetchData } from "../api";
import Container from "react-bootstrap/Container";
import Button from "react-bootstrap/Button";

export const Random = () => {
  const [data, setData] = useState([]);
  const [userAnswers, setUserAnswers] = useState([]); // To store the user's answers

  useEffect(() => {
    getData();
  }, []);

  const getData = async () => {
    const response = await fetchData('mcq/r/django');
    setData(response);
  };

  const handleAnswerClick = (questionIndex, answerIndex) => {
    // Create a copy of the userAnswers array
    const newAnswers = [...userAnswers];
    newAnswers[questionIndex] = answerIndex;
    setUserAnswers(newAnswers);
  };

  return (
    <Container>
      {data.map((question, questionIndex) => (
        <div key={questionIndex}>
          <h3>
            <label htmlFor="">{questionIndex + 1}) {question.title}</label>
          </h3>
          <ul className="answer-list">
            {question.answer.map((answer, answerIndex) => (
              <div key={answer.id}>
                <form action="">
                  <br />
                  <div className="main">
                    <Button
                      variant={
                        userAnswers[questionIndex] === answerIndex
                          ? answer.is_right
                            ? "success"
                            : "danger"
                          : "primary"
                      }
                      onClick={() => handleAnswerClick(questionIndex, answerIndex)}
                    >
                      {answerIndex + 1}) {answer.answer_text}
                      {answer.is_right ? " (Correct)" : ""}
                    </Button>
                  </div>
                </form>
              </div>
            ))}
          </ul>
        </div>
      ))}
    </Container>
  );
};

export default Random;
