import React, { useEffect, useState } from "react";
import { fetchData } from "../api";
import Container from "react-bootstrap/Container";
import Button from "react-bootstrap/Button";

export const Random = () => {
  const [data, setData] = useState([]);
  const [userAnswers, setUserAnswers] = useState([]); // To store the user's answers
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0); // To track the current question
  const [answerSelected, setAnswerSelected] = useState(false); // To track if an answer has been selected

  useEffect(() => {
    getData();
  }, []);

  const getData = async () => {
    const response = await fetchData('mcq/q/django');
    setData(response);
  };

  const handleAnswerClick = (questionIndex, answerIndex) => {
    // Create a copy of the userAnswers array
    const newAnswers = [...userAnswers];
    newAnswers[questionIndex] = answerIndex;
    setUserAnswers(newAnswers);
    setAnswerSelected(true);
  };

  const handleNextClick = () => {
    if (currentQuestionIndex < data.length - 1) {
      setCurrentQuestionIndex(currentQuestionIndex + 1);
      setAnswerSelected(false); // Reset answerSelected state for the next question
    }
  };

  return (
    <Container>
      {data.length > 0 && (
        <div>
          <h3>
            <label htmlFor="">{currentQuestionIndex + 1}) {data[currentQuestionIndex].title}</label>
          </h3>
          <ul className="answer-list">
            {data[currentQuestionIndex].answer.map((answer, answerIndex) => (
              <div key={answer.id}>
                <form action="">
                  <br />
                  <div className="main">
                    <Button
                      variant={
                        userAnswers[currentQuestionIndex] === answerIndex
                          ? answer.is_right
                            ? "success"
                            : "danger"
                          : "primary"
                      }
                      onClick={() => handleAnswerClick(currentQuestionIndex, answerIndex)}
                      disabled={answerSelected} // Disable the button if an answer has been selected
                    >
                      {answerIndex + 1}) {answer.answer_text}
                      {answer.is_right ? " " : ""}
                    </Button>
                  </div>
                </form>
              </div>
            ))}
          </ul>
          {answerSelected && currentQuestionIndex < data.length - 1 && (
            <Button onClick={handleNextClick} variant="primary">
              Next
            </Button>
          )}
        </div>
      )}
    </Container>
  );
};

export default Random;
