import  { useEffect, useState } from 'react';
import axios from 'axios';
 const [isFetching, setFetching] = useState(false);
    const [dataState, setDataState] = useState({ data: [] });
    const apiUrl = url; // No need to use useState for the API URL

const CollectData = (url) => {
   
    useEffect(() => {
        console.log("Mounting or Re-rendering...");
        const fetchDataFromApi = async () => {
            try {
                setFetching(true); // Update isFetching state to true

                const response = await axios.get(apiUrl);

                setDataState({ data: response.data }); // Update dataState with the response data
                
                setFetching(false); // Set isFetching back to false when done fetching
            } catch (e) {
                console.log(e);
                setFetching(false); // Make sure to set isFetching to false in case of an error
            }
        };

        fetchDataFromApi();
    }, [apiUrl]); // Include apiUrl in the dependency array to re-fetch when it changes

    return [dataState, isFetching]; // Return isFetching state as well for better control
};

export default CollectData;
